import React, { useEffect, useState } from "react";
import "./singleProduct.scss";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectPopularProductsCollections } from "../../redux//popularProducts/popularProduct.selector";
import { selectCurrentUser } from "../../redux/user/user.selectors";

import CustomButton from "../../components/common/customButton/customButton";
import Spinner from "../../components/common/spinner/spinner.component";
import CommentSection from "../../components/common/ comment-section/commentSection";

import { addItem } from "../../redux/cart/cartActions";

import { firestore, firebase } from "../../firebase/firebase";
import { useParams } from "react-router-dom";

import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";

const SingleProduct = ({ addItem, collections, currentUser }) => {
  const { id } = useParams();
  const [photo, setPhoto] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(true);
  const [sizes, setSizes] = useState([]);
  const [size, setSize] = useState("");
  const [color, setColor] = useState("");
  const [colors, setColors] = useState([]);
  const [comment, setComment] = useState("");
  const [posts, setPosts] = useState([]);
  let db = firestore.collection("allProducts").doc(id);

  console.log(posts);

  const addProductToCart = () => {
    db.get()
      .then((data) => {
        setPhoto(data.data().imageUrl);
        setName(data.data().name);
        setPrice(data.data().price);
        setText(data.data().text);
        setSizes(data.data().size);
        setColors(data.data().color);
      })
      .then(() => {
        setLoading(false);
      });
  };

  const addComment = () => {
    db.collection("reviews")
      .add({
        comment: comment,
        time: firebase.firestore.Timestamp.fromDate(new Date()),
        user: currentUser.displayName,
      })
      .then(() => alert("done"));
  };

  const getComment = () => {
    db.collection("reviews")
      .get()
      .then((data) => {
        const array = [];
        data.forEach((doc) => {
          const obj = {
            comment: doc.data().comment,
            userName: doc.data().user,
            postedTime: doc.data().time.toDate(),
          };
          array.push(obj);
        });
        setPosts(array);
      });
  };

  useEffect(() => {
    addProductToCart();
    getComment();
  }, []);

  return (
    <div>
      <Spinner isLoading={loading} />
      <div className="single-product">
        <div className="photo">
          <Carousel showArrows={true}>
            {photo.map((image) => (
              <div key={image.id}>
                <img src={image} alt="slide-show picture" />
              </div>
            ))}
          </Carousel>
        </div>
        <div className="product-details">
          <h1>{name}</h1>
          <p>{text}</p>
          <div className="product-size">
            <select value={size} onChange={(e) => setSize(e.target.value)}>
              <option value="">Please select a size</option>
              {sizes.map((item) => (
                <option value={item} key={item.id}>
                  {item}
                </option>
              ))}
            </select>
            <select value={color} onChange={(e) => setColor(e.target.value)}>
              <option value="">Please select a color</option>
              {colors.map((item) => (
                <option value={item} key={item.id}>
                  {item}
                </option>
              ))}
            </select>

            <h3>Price: &euro;{price}</h3>
            <CustomButton
              onClick={() =>
                addItem({
                  ...collections.find((el) => el.id === id),
                  selectedColor: color,
                  selectedSize: size,
                })
              }
            >
              ADD TO CART
            </CustomButton>
          </div>
        </div>
      </div>
      <div className="comment-section">
        <h1>Product reviews:</h1>
        {posts.map((post) => (
          <div className="comment">
            <h3>{post.userName}</h3>
            <p>{post.comment}</p>
            {/* <p>{post.postedTime.toDate()}</p> */}
          </div>
          //    <CommentSection
          //    name={post.userName}
          //    comment={post.comment}
          //    postedTime={post.postedTime}
          //  />
        ))}
        <div className="text">
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Share your experience"
          ></textarea>
        </div>
        <div className="comment-button">
          <button onClick={() => addComment()}>Submit</button>
        </div>
      </div>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

const mapStateToProps = createStructuredSelector({
  collections: selectPopularProductsCollections,
  currentUser: selectCurrentUser,
});

export default connect(mapStateToProps, mapDispatchToProps)(SingleProduct);
