import React, {useState} from "react";
import "./sign-in.scss";

import FormInput from "../../components/common/form-input/formInput.component";
import CustomButton from "../../components/common/customButton/customButton";

import { auth, createUserProfile } from "../../firebase/firebase";

const SignIn = () => {
  const [userInfo, setUserInfo] = useState()

  handleSubmit = async (e) => {
    e.preventDefault();

    const { displayName, email, password, confirmPassword } = this.state;

    if (password !== confirmPassword) {
      this.setState({ message: "Passwords do not match", display: false });
      return;
    }

    try {
      const { user } = await auth.createUserWithEmailAndPassword(
        email,
        password
      );
      await createUserProfile(user, { displayName });

      this.setState({
        displayName: "",
        email: "",
        password: "",
        confirmPassword: "",
        // message: "You have successfully registered",
        // display: true,
      });
    } catch (e) {
      // this.setState({ message: e.message, display: false });
      alert(e.message);
    }
  };

  handleChange = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value });
  };

    const { displayName, email, password, confirmPassword } = this.state;
    return (
      <div className="sign-up-content">
        <div className="sign-up animate__animated animate__bounceInDown">
          <h2 className="title">Welcome, please sign up</h2>
          <form className="sign-up-form" onSubmit={this.handleSubmit}>
            <FormInput
              type="text"
              name="displayName"
              value={displayName}
              handleChange={this.handleChange}
              label="DisplayName"
              required
            ></FormInput>
            <FormInput
              type="email"
              name="email"
              value={email}
              handleChange={this.handleChange}
              label="Email"
              required
            ></FormInput>
            <FormInput
              type="password"
              name="password"
              value={password}
              handleChange={this.handleChange}
              label="Password"
              required
            ></FormInput>
            <FormInput
              type="password"
              name="confirmPassword"
              value={confirmPassword}
              handleChange={this.handleChange}
              label="Confirm Password"
              required
            ></FormInput>
            <CustomButton type="submit">SIGN UP</CustomButton>
          </form>
        </div>
      </div>
    );
}

export default SignIn;
