                  link: "https://www.google.com/",
                },
                {
                  title: "Other",
                  link: "https://www.google.com/",
                },
              ],
            },
          ],
        },
        {
          id: "3",
          title: "About Us",
          link: "https://www.google.com/",
        },
      ],
      otherLinks: [
        {
          title: "Other",
          link: "https://www.google.com/",
        },
      ],
    };
    this.navigationBarRef = React.createRef();
  }

  closeDropdown = (e) => {
    let element = e.target;

    console.log(element);

    // this.setState({
    //   navigationLinks: this.state.navigationLinks.map((el) => {
    //     return {
    //       ...el,
    //       showMenu: false,
    //     };
    //   }),
    // });
  };

  toggleDropdown = (id) => {
    const linkIndex = this.state.navigationLinks.findIndex(
      (el) => el.id === id
    );
    this.setState({
      navigationLinks: this.state.navigationLinks.map((el, idx, arr) => {
        return {
          ...el,
          showMenu: el.id === id && !arr[idx].showMenu,
        };
      }),
    });
  };

  componentDidMount() {
    window.addEventListener("click", this.closeDropdown);
  }

  componentWillMount() {
    window.removeEventListener("click", this.closeDropdown);
  }

  render() {
    const navigationBarLinks = this.state.navigationLinks.map((navLinkObj) => (
      <NavigationBarLink
        key={navLinkObj.id}
        {...navLinkObj}
        {...(() =>
          navLinkObj.categories
            ? {
                handleChange: () => this.toggleDropdown(navLinkObj.id),
                otherLinks: this.state.otherLinks,
              }
            : null)()}
      />
    ));
    return (
      <div className="navigation-bar" ref={this.navigationBarRef}>
        {navigationBarLinks}
      </div>
    );
  }
}

export default NavigationBar;
