import React from "react";
import NavigationBarLink from "../navigation-link/navigationBarLink";

import "./navigation-bar.scss";

class NavigationBar extends React.Component {
  constructor() {
    super();
    this.state = {
      navigationLinks: [
        {
          id: "1",
          title: "For Women",
          showMenu: false,
          categories: [
            {
              title: "See all",
              link: "https://www.google.com/",
            },
            {
              title: "Clothes",
              subcatogories: [
                {
                  title: "See All",
                  link: "https://www.google.com/",
                },
                {
                  title: "Sweaters",
                  link: "https://www.google.com/",
                },
                {
                  title: "Skirts",
                  link: "https://www.google.com/",
                },
                {
                  title: "Jeans",
                  link: "https://www.google.com/",
                },
              ],
              title: "Shoes",
              subcatogories: [
                {
                  title: "High Heels",
                  link: "https://www.google.com/",
                },
                {
                  title: "Sneakers",
                  link: "https://www.google.com/",
                },
                {
                  title: "Boots",
                  link: "https://www.google.com/",
                },
              ],
              title: "Accessories",
              subcatogories: [
                {
                  title: "Glasses",
                  link: "https://www.google.com/",
                },
                {
                  title: "Piercings",
                  link: "https://www.google.com/",
                },
                {
                  title: "Other",
                  link: "https://www.google.com/",
                },
              ],
            },
          ],
        },
        {
          id: "2",
          title: "For Men",
          showMenu: false,
          categories: [
            {
              title: "See all",
              link: "https://www.google.com/",
            },
            {
              title: "Clothes",
              subcatogories: [
                {
                  title: "See All",
                  link: "https://www.google.com/",
                },
                {
                  title: "Sweaters",
                  link: "https://www.google.com/",
                },
                {
                  title: "Skirts",
                  link: "https://www.google.com/",
                },
                {
                  title: "Jeans",
                  link: "https://www.google.com/",
                },
              ],
              title: "Shoes",
              subcatogories: [
                {
                  title: "Sandals",
                  link: "https://www.google.com/",
                },
                {
                  title: "Sneakers",
                  link: "https://www.google.com/",
                },
                {
                  title: "Boots",
                  link: "https://www.google.com/",
                },
              ],
              title: "Accessories",
              subcatogories: [
                {
                  title: "Glasses",
                  link: "https://www.google.com/",
                },
                {
                  title: "Piercings",
                  link: "https://www.google.com/",
                },
                {
                  title: "Other",
                  link: "https://www.google.com/",
                },
              ],
            },
          ],
        },
        {
          id: "3",
          title: "About Us",
          link: "https://www.google.com/",
        },
      ],
      otherLinks: [
        {
          title: "Other",
          link: "https://www.google.com/",
        },
      ],
    };
  }

  toggleDropdown = (id) => {
    const linkIndex = this.state.navigationLinks.findIndex(
      (el) => el.id === id
    );

    if(this.state.navigationLinks[linkIndex].showMenu) {
      this.setState({
        navigationLinks: this.state.navigationLinks.p
      })
    }
  };

  render() {
    const navigationBarLinks = this.state.navigationLinks.map((props) => (
      <NavigationBarLink
        key={props.id}
        {...props}
        handleChange={
          props.categories ? () => this.toggleDropdown(props.id) : null
        }
      />
    ));
    return (
      <div className="container">
        <div className="navigation-bar">{navigationBarLinks}</div>
      </div>
    );
  }
}

export default NavigationBar;
